extension CardsPresenter: CardPresenterProtocol {
    func loadImage(withIndex index: Int) {
        guard let path = cards?.cards?[index].image else { return }
        let url = URL(fileURLWithPath: path)
        viewCell.cardImage.load(url: url)
    }
